import React, { useState } from "react";
import { Button, Form, FormGroup, Input } from "reactstrap";
import { NavLink } from "react-router-dom";
import axios from "axios";
import BarGraph from "../../component/BarGraph";
import ChevronRight from "../../assets/images/chevron-right.png";

const Home = () => {
  const initialValues = {
    monthInput: null,
  };

  const [values, setValues] = useState(initialValues);
  const [dataReport, setDataReport] = useState(null);

  const handleInputChange = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    });
    // console.log(values.monthInput); cek isi value onChange
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const urlAPI = "https://bootcamp-rent-cars.herokuapp.com";
    const config = {
      headers: {
        access_token: `${localStorage.getItem("ACCESS_TOKEN")}`,
      },
    };

    axios
      .get(
        `${urlAPI}/admin/order/reports?from=${values.monthInput}-01&until=${values.monthInput}-31`,
        config
      )
      .then(async (res) => {
        const tempReports = res.data;
        setDataReport(tempReports);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  let activeClassName = "menuItemActive";
  return (
    <main id="pageDashboard">
      <div className="container-fluid">
        <div className="row">
          <div className="col-lg-2 leftSidebar">
            <h2 className="pageTitle">DASHBOARD</h2>
            <NavLink
              to="#dashboard"
              className={({ isActive }) =>
                isActive ? activeClassName : "menuItem"
              }
            >
              Dashboard
            </NavLink>
          </div>
          <div className="col-lg-10 rightContent">
            <div className="breadcrumb d-flex align-items-center">
              <h4 className="breadcrumbRoot">Dashboard</h4>
              <img
                src={ChevronRight}
                className="breadcrumbArrow img-fluid"
                alt="Breadcrumb Arrow"
              ></img>
              <h4 className="breadcrumbCurrent">Dashboard</h4>
            </div>
            <section id="sectionBarGraph">
              <div className="container-fluid">
                <div className="row">
                  <div className="col-lg-12">
                    <div className="titleArea d-flex align-items-center">
                      <h3 className="titleText">
                        Rented Car Data Visualization
                      </h3>
                    </div>
                    <h5 className="formTitle">Month</h5>
                    <Form
                      id="monthFilterForm"
                      className="d-flex"
                      onSubmit={handleSubmit}
                    >
                      <FormGroup>
                        <Input
                          required
                          name="monthInput"
                          id="monthInput"
                          className="monthInput"
                          type="month"
                          as="input"
                          placeholder="Pilih Bulan"
                          onChange={handleInputChange}
                        />
                      </FormGroup>
                      <Button className="submitButton" type="submit">
                        Go
                      </Button>
                    </Form>
                    {dataReport ? (
                      <BarGraph dataReport={dataReport} />
                    ) : (
                      <h4 className="dataEmptyAlert">
                        Sorry, but the data is empty, Please select month report
                        to be displayed
                      </h4>
                    )}
                  </div>
                </div>
              </div>
            </section>
          </div>
        </div>
      </div>
    </main>
  );
};
export default Home;
